import { useState, useEffect } from "react";
import axios from "axios";
import MyIngredients from "./myingredients";

const HomePage = () => {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [showIngredientsModal, setShowIngredientsModal] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:6409/api/recipestores")
      .then((response) => {
        console.log("Fetched Data:", response.data);
        setRecipes(response.data);
      })
      .catch((error) => console.error("Error fetching recipes:", error));
  }, []);

  const openModal = (recipe) => {
    setSelectedRecipe(recipe);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedRecipe(null);
    setShowIngredientsModal(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-4xl font-bold text-center mb-6 text-gray-800">
        Recipe Gallery
      </h1>

      {/* Recipe Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 max-w-7xl mx-auto">
        {recipes.length > 0 ? (
          recipes.map((recipe) => (
            <div
              key={recipe._id}
              className="bg-white shadow-lg rounded-lg overflow-hidden transform transition-transform hover:scale-105 cursor-pointer"
              onClick={() => openModal(recipe)}
            >
              <img
                src={recipe.image}
                alt={recipe.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <h2 className="text-xl font-semibold text-gray-800">{recipe.name}</h2>
                <p className="text-gray-600 text-sm truncate">{recipe.description}</p>
                <p className="text-lg font-bold text-green-600 mt-2">${recipe.price}</p>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">Loading recipes...</p>
        )}
      </div>

      {/* Recipe Details Modal */}
      {isModalOpen && selectedRecipe && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 p-4">
          <div className="bg-white rounded-lg shadow-lg max-w-lg w-full p-6 relative">
            <button
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-800"
              onClick={closeModal}
            >
              âœ–
            </button>
            <h2 className="text-2xl font-semibold text-gray-800">{selectedRecipe.name}</h2>
            <img
              src={selectedRecipe.image}
              alt={selectedRecipe.name}
              className="w-full h-56 object-cover mt-4 rounded"
            />
            <p className="text-gray-700 mt-4">{selectedRecipe.description}</p>
            <p className="text-lg font-bold text-orange-600 mt-2">Price: ${selectedRecipe.price}</p>

            {/* Watch Video Button */}
            {selectedRecipe.videoUrl && (
              <a
                href={selectedRecipe.videoUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="block bg-blue-500 text-white text-center font-semibold py-2 rounded-md mt-4 hover:bg-blue-600 transition"
              >
                ðŸŽ¥ Watch Video
              </a>
            )}

            {/* View Ingredients Button */}
            <button
              className="block bg-green-500 text-white font-semibold py-2 rounded-md mt-3 hover:bg-green-600 transition w-full"
              onClick={() => setShowIngredientsModal(true)}
            >
              View Ingredients
            </button>
          </div>
        </div>
      )}

      {/* Ingredients Modal */}
      {showIngredientsModal && selectedRecipe && (
        <MyIngredients
          ingredients={selectedRecipe.ingredients}
          onClose={() => setShowIngredientsModal(false)}
        />
      )}
    </div>
  );
};

export default HomePage;
