import React, { useState } from "react";
//import axios from "axios";
import"./Register.css";


function RegisterPage() {
  const [formData, setFormData] = useState({ name: "", email: "", password: "" });

  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:6409/register", formData);
      if (response.status === 201) alert(response.data.message);
      console.log(response);
    } catch (error) {
      console.error("Registration failed:", error);
    }
  }

  return React.createElement(
    "div",
    { className: "min-h-screen flex items-center justify-center bg-gray-100" },
    React.createElement(
      "div",
      { className: "w-full max-w-md bg-white rounded-lg shadow-md p-6" },
      React.createElement("h2", { className: "text-2xl font-bold text-center mb-6" }, "Register"),
      React.createElement(
        "form",
        { onSubmit: handleSubmit, className: "space-y-4" },
        ["name", "email", "password"].map((field) =>
          React.createElement(
            "div",
            { key: field },
            React.createElement(
              "label",
              { htmlFor: field, className: "block text-sm font-medium text-gray-700" },
              field.charAt(0).toUpperCase() + field.slice(1)
            ),
            React.createElement("input", {
              type: field === "password" ? "password" : "text",
              id: field,
              name: field,
              value: formData[field],
              onChange: handleChange,
              placeholder: `Enter your ${field}`,
              className:
                "mt-1 block w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-blue-500 focus:border-blue-500",
              required: true,
            })
          )
        ),
        React.createElement(
          "button",
          {
            type: "submit",
            className:
              "w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 focus:ring-4 focus:ring-blue-300 focus:outline-none",
          },
          "Register"
        )
      )
    )
  );
}

export default RegisterPage;
