import { useState, useEffect } from "react";
import axios from "axios";

const HomePage = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:6409/api/recipestores")
      .then(response => {
        console.log("Fetched Data:", response.data);
        setRecipes(response.data);
      })
      .catch(error => console.error("Error fetching recipes:", error));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Recipe Gallery</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-4xl mx-auto">
        {recipes.length > 0 ? (
          recipes.map((recipe) => (
            <div key={recipe._id} className="bg-white p-4 rounded-xl shadow-md text-center">
              <img
                src={recipe.image}
                alt={recipe.name}
                className="w-full h-40 object-cover rounded-lg"
              />
              <h2 className="mt-3 text-lg font-semibold">{recipe.name}</h2>
              <p className="text-sm text-gray-500">{recipe.description}</p>
              <p className="mt-2 text-xl font-semibold">${recipe.price}</p>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">Loading recipes...</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
