// File: src/EmployeeForm.js
import React, { useState, useEffect } from "react";
import "./EmployeeForm.css";

const EmployeeForm = ({ addEmployee, editEmployee, updateEmployee }) => {
  const [employee, setEmployee] = useState({
    name: "",
    position: "",
    department: "",
    joiningDate: "",
    salary: "",
  });

  useEffect(() => {
    if (editEmployee) {
      setEmployee(editEmployee);
    } else {
      setEmployee({
        name: "",
        position: "",
        department: "",
        joiningDate: "",
        salary: "",
      });
    }
  }, [editEmployee]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployee({ ...employee, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editEmployee) {
      updateEmployee(employee);
    } else {
      addEmployee(employee);
    }
    setEmployee({
      name: "",
      position: "",
      department: "",
      joiningDate: "",
      salary: "",
    });
  };

  return (
    <form onSubmit={handleSubmit} className="employee-form">
      <input type="text" name="name" value={employee.name} onChange={handleChange} placeholder="Name" required />
      <input type="text" name="position" value={employee.position} onChange={handleChange} placeholder="Position" required />
      <input type="text" name="department" value={employee.department} onChange={handleChange} placeholder="Department" required />
      <input type="date" name="joiningDate" value={employee.joiningDate} onChange={handleChange} required />
      <input type="number" name="salary" value={employee.salary} onChange={handleChange} placeholder="Salary" required />
      <button type="submit">{editEmployee ? "Update" : "Add"} Employee</button>
    </form>
  );
};

export default EmployeeForm;
