import { useState, useEffect } from "react";
import axios from "axios";
import "./Home.css"
const HomePage = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:6409/api/recipestores")
      .then((response) => {
        console.log("Fetched Data:", response.data);
        setRecipes(response.data);
      })
      .catch((error) => console.error("Error fetching recipes:", error));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Recipe Gallery</h1>
      <div className="max-w-4xl mx-auto space-y-6">
        {recipes.length > 0 ? (
          recipes.map((recipe) => (
            <div
              key={recipe._id}
              className="flex flex-col sm:flex-row bg-white rounded-xl shadow-md overflow-hidden"
            >
              {/* Left: Recipe Image */}
              <div className="sm:w-1/2">
                <img
                  src={recipe.image}
                  alt={recipe.name}
                  className="w-full h-40 sm:h-full object-cover"
                />
              </div>

              {/* Right: Recipe Details */}
              <div className="sm:w-1/2 p-4 flex flex-col justify-center">
                <h2 className="text-lg font-semibold mb-2">{recipe.name}</h2>
                <p className="text-sm text-gray-500 mb-2">{recipe.description}</p>
                <p className="text-xl font-semibold">${recipe.price}</p>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">Loading recipes...</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
