import React, { useState } from "react";
import axios from "axios";
import "./Register.css";

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const [error, setError] = useState(""); // To store error message

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous errors

    try {
      const response = await axios.post("http://localhost:6409/register", formData);
      if (response.status === 201) {
        alert(response.data.message);
        // Optionally reset form fields
        setFormData({ name: "", email: "", password: "" });
      }
    } catch (error) {
      console.error("There was an error!", error);
      // Handle specific error based on the response
      if (error.response && error.response.data) {
        setError(error.response.data.message); // Display error message from the backend
      } else {
        setError("An unexpected error occurred.");
      }
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="register-container w-full max-w-md bg-white rounded-lg shadow-md p-6">
        <h2 className="register-title text-2xl font-bold text-center mb-6">Register</h2>
        {error && <div className="error-message text-red-500 text-center mb-4">{error}</div>} {/* Error message display */}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="name" className="label-text">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your name"
              className="input-field"
              required
            />
          </div>
          <div>
            <label htmlFor="email" className="label-text">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              className="input-field"
              required
            />
          </div>
          <div>
            <label htmlFor="password" className="label-text">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
              className="input-field"
              required
            />
          </div>
          <button
            type="submit"
            className="submit-btn"
          >
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterPage;
