import { useState, useEffect } from "react";
import axios from "axios";
import "./Home.css";
//import MyIngredients from "./MyIngredients"; // Ensure this component exists

const HomePage = () => {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [showIngredientsModal, setShowIngredientsModal] = useState(false);
  const [error, setError] = useState(null);

  // Fetch Recipes on Mount
  useEffect(() => {
    let isMounted = true;
    axios.get("http://localhost:6409/api/recipestores")
      .then((response) => {
        if (isMounted) {
          if (Array.isArray(response.data)) {
            setRecipes(response.data);
          } else {
            setError("Unexpected API response format.");
          }
        }
      })
      .catch(() => isMounted && setError("Error fetching recipes."));

    return () => { isMounted = false; };
  }, []);

  // Function to Add a New Recipe
  const addRecipe = (newRecipe) => {
    axios.post("http://localhost:6409/api/recipestores", newRecipe)
      .then((response) => {
        if (response.data) {
          setRecipes(prevRecipes => [...prevRecipes, response.data]); // Append only the new recipe
        }
      })
      .catch((error) => console.error("Error adding recipe:", error));
  };

  const openModal = (recipe) => {
    setSelectedRecipe(recipe);
    setIsModalOpen(true);
    setShowIngredientsModal(false);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedRecipe(null);
    setShowIngredientsModal(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Recipe Gallery</h1>

      <button
        className="add-recipe-button"
        onClick={() => addRecipe({
          name: "New Dish",
          image: "https://via.placeholder.com/150",
          description: "A delicious new recipe!",
          price: 15.99,
        })}
        aria-label="Add a new recipe"
      >
        ➕ Add Recipe
      </button>

      {error && <p className="text-center text-red-500">{error}</p>}

      <div className="recipe-grid-container">
        {recipes.length > 0 ? (
          recipes.map((recipe) => (
            <div
              key={recipe._id}
              className="recipe-card"
              onClick={() => openModal(recipe)}
            >
              <img src={recipe.image} alt={recipe.name} className="recipe-image" />
              <div className="recipe-details">
                <h2 className="recipe-name">{recipe.name}</h2>
                <p className="recipe-description">{recipe.description}</p>
                <p className="recipe-price">${recipe.price}</p>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">No recipes found.</p>
        )}
      </div>

      {/* Recipe Modal */}
      {isModalOpen && selectedRecipe && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-button" onClick={closeModal} aria-label="Close modal">✖</button>
            <h2 className="modal-title">{selectedRecipe.name}</h2>
            <img src={selectedRecipe.image} alt={selectedRecipe.name} className="modal-image" />
            <p className="modal-description">{selectedRecipe.description}</p>
            <p className="modal-price">Price: ${selectedRecipe.price}</p>

            {selectedRecipe.videoUrl && (
              <a href={selectedRecipe.videoUrl} target="_blank" rel="noopener noreferrer" className="video-button">
                🎥 Watch Video
              </a>
            )}

            {selectedRecipe.ingredients && selectedRecipe.ingredients.length > 0 && (
              <button 
                className="ingredients-button" 
                onClick={() => setShowIngredientsModal(true)} 
                aria-label="View Ingredients"
              >
                View Ingredients
              </button>
            )}
          </div>
        </div>
      )}

      {/* Ingredients Modal */}
      {showIngredientsModal && selectedRecipe?.ingredients && (
        <MyIngredients ingredients={selectedRecipe.ingredients} onClose={() => setShowIngredientsModal(false)} />
      )}
    </div>
  );
};

export default HomePage;
