import React, { useState, useEffect } from "react";

const Home = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch recipe stores data from the backend API
    fetch('http://localhost:6409/api/recipestores')
      .then((response) => response.json())
      .then((data) => {
        setRecipes(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching recipe stores:', error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Recipe Gallery</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-4xl mx-auto">
        {loading ? (
          <p className="text-center text-gray-500">Loading recipes...</p>
        ) : recipes.length > 0 ? (
          recipes.map((recipe) => (
            <div key={recipe._id} className="bg-white p-4 rounded-xl shadow-md text-center">
              <img
                src={recipe.image}
                alt={recipe.name}
                className="w-full h-40 object-cover rounded-lg"
              />
              <h2 className="mt-3 text-lg font-semibold">{recipe.name}</h2>
              <p className="text-sm text-gray-500">{recipe.description}</p>
              <p className="mt-2 text-xl font-semibold">${recipe.price}</p>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">No recipes available.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
