import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./AddRecipe.css"; // Link the new CSS file

const AddRecipe = () => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    image: "",
    price: "",
  });

  const [loading, setLoading] = useState(false);

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: name === "price" ? value.replace(/[^0-9.]/g, "") : value, // Restrict non-numeric input for price
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const token = localStorage.getItem("token");

    // Validate form data
    if (!formData.name || !formData.description || !formData.image || !formData.price) {
      toast.error("Please fill in all fields.");
      setLoading(false);
      return;
    }

    // Ensure token exists
    if (!token) {
      toast.error("Unauthorized: No token found! Please log in.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:6409/api/recipestores", // API URL
        { ...formData, price: parseFloat(formData.price) }, // Ensure price is a number
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 201) {
        toast.success("Recipe added successfully!");
        setFormData({ name: "", description: "", image: "", price: "" }); // Reset form
      }
    } catch (error) {
      console.error("Error adding recipe:", error);
      toast.error(error.response?.data?.message || "Failed to add recipe");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="recipe-container">
      <h2 className="recipe-title">Add a New Recipe</h2>
      <form onSubmit={handleSubmit} className="recipe-form">
        <div className="recipe-input-group">
          <label className="recipe-label">Recipe Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            placeholder="Enter recipe name"
            className="recipe-input"
            required
          />
        </div>
        <div className="recipe-input-group">
          <label className="recipe-label">Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            placeholder="Enter recipe description"
            className="recipe-textarea"
            required
          />
        </div>
        <div className="recipe-input-group">
          <label className="recipe-label">Image URL</label>
          <input
            type="text"
            name="image"
            value={formData.image}
            onChange={handleInputChange}
            placeholder="Enter image URL"
            className="recipe-input"
            required
          />
        </div>
        <div className="recipe-input-group">
          <label className="recipe-label">Price</label>
          <input
            type="text"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            placeholder="Enter recipe price"
            className="recipe-input"
            required
          />
        </div>
        <button type="submit" className="recipe-button" disabled={loading}>
          {loading ? "Submitting..." : "Submit Recipe"}
        </button>
      </form>
    </div>
  );
};

export default AddRecipe;
