import React from "react";
import "./myingredient.css";

const MyIngredients = ({ ingredients, onClose }) => {
  const defaultIngredients = [
    "1 cup all-purpose flour",
    "2 eggs",
    "1/2 cup sugar",
    "1/4 cup butter",
    "1 tsp vanilla extract",
    "1/2 cup milk",
    "1 tsp baking powder",
    "Pinch of salt",
  ];

  const displayIngredients = ingredients && ingredients.length > 0 ? ingredients : defaultIngredients;

  return (
    <div className="ingredients-modal-overlay" onClick={onClose}>
      <div className="ingredients-modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="ingredients-close-button" onClick={onClose}>
          <span>âœ–</span>
        </button>
        <h2 className="ingredients-modal-title">Ingredients</h2>
        <ul className="ingredients-modal-list">
          {displayIngredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MyIngredients;
