import React from "react";
import "./myingredient.css";

const MyIngredients = ({ ingredients, onClose }) => {
  if (!ingredients || ingredients.length === 0) {
    return null; // Don't show empty modal
  }

  return (
    <div className="ingredients-modal-overlay" onClick={onClose}>
      <div className="ingredients-modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="ingredients-close-button" onClick={onClose}>âœ–</button>
        <h2 className="ingredients-modal-title">Ingredients</h2>
        <ul className="ingredients-modal-list">
          {ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MyIngredients;
