import React, { useState } from 'react';
//import './front.css'; // Import the external CSS file

const SubmissionRecipe = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !description || !image || !price) {
      alert('All fields are required!');
      return;
    }

    const newRecipe = { name, description, image, price };

    try {
      const response = await fetch('http://localhost:6409/api/ecipestores', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newRecipe),
      });

      if (!response.ok) {
        throw new Error('Failed to add recipe');
      }

      alert('Recipe added successfully!');
      setName('');
      setDescription('');
      setImage('');
      setPrice('');
    } catch (error) {
      console.error('Error adding recipe:', error);
      alert('Failed to add recipe');
    }
  };

  return (
    <div className="add-product-container">
      <h2 className="add-product-title">Add New Recipe</h2>
      <form onSubmit={handleSubmit} className="add-product-form">
        <input
          type="text"
          placeholder="Recipe Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="add-product-input"
        />
        <textarea
          placeholder="Recipe Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="add-product-textarea"
        ></textarea>
        <input
          type="text"
          placeholder="Image URL"
          value={image}
          onChange={(e) => setImage(e.target.value)}
          className="add-product-input"
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          className="add-product-input"
        />
        <button type="submit" className="add-product-button">
          Add Recipe
        </button>
      </form>
    </div>
  );
};

export default SubmissionRecipe;
