import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './homepage.css';

const Homepage = () => {
  const [recipeStores, setRecipeStores] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRecipeStores = async () => {
      try {
        const response = await axios.get('http://localhost:6409/api/recipestores');
        setRecipeStores(response.data);
      } catch (error) {
        console.error('Error fetching recipe stores:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipeStores();
  }, []);

  return (
    <div className="homepage-container">
      <header className="homepage-header">
        <h1>Recipe Stores</h1>
        <p>Discover delicious recipes from your favorite stores!</p>
      </header>

      {loading ? (
        <div className="loader"></div>
      ) : recipeStores.length === 0 ? (
        <p className="empty-message">No recipe stores found.</p>
      ) : (
        <div className="recipe-grid">
          {recipeStores.map((store) => (
            <div className="recipe-card" key={store._id}>
              <div className="image-wrapper">
                <img src={store.image} alt={store.name} />
              </div>
              <div className="card-content">
                <h3>{store.name}</h3>
                <p>{store.description}</p>
                <p className="price">â‚¹{store.price}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Homepage;
