import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const AddRecipe = () => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    image: "",
    price: "",
    ingredients: "",
  });

  const [loading, setLoading] = useState(false);

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: name === "price" ? value.replace(/[^0-9.]/g, "") : value,
    }));
  };

  // Validate form data
  const isFormValid = () => {
    const { name, description, image, price, ingredients } = formData;
    if (!name || !description || !image || !price || !ingredients) {
      toast.error("Please fill in all fields.");
      return false;
    }
    return true;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isFormValid()) return;

    setLoading(true);
    const token = localStorage.getItem("token");

    if (!token) {
      toast.error("Unauthorized: No token found! Please log in.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:6409/api/recipestores",
        {
          ...formData,
          price: parseFloat(formData.price),
          ingredients: formData.ingredients.split(",").map((item) => item.trim()), // Convert to array
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // ✅ Fixed template literal
          },
        }
      );

      if (response.status === 201) {
        toast.success("Recipe added successfully!");
        setFormData({ name: "", description: "", image: "", price: "", ingredients: "" });
      }
    } catch (error) {
      console.error("Error adding recipe:", error);
      toast.error(error.response?.data?.message || "Failed to add recipe");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Add a New Recipe</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <InputField label="Recipe Name" type="text" name="name" value={formData.name} placeholder="Enter recipe name" onChange={handleInputChange} />
        <TextAreaField label="Description" name="description" value={formData.description} placeholder="Enter recipe description" onChange={handleInputChange} />
        <InputField label="Image URL" type="text" name="image" value={formData.image} placeholder="Enter image URL" onChange={handleInputChange} />
        <InputField label="Price" type="text" name="price" value={formData.price} placeholder="Enter recipe price" onChange={handleInputChange} />
        <InputField label="Ingredients" type="text" name="ingredients" value={formData.ingredients} placeholder="Enter ingredients (comma-separated)" onChange={handleInputChange} />

        <button type="submit" style={styles.button} disabled={loading}>
          {loading ? "Submitting..." : "Submit Recipe"}
        </button>
      </form>
    </div>
  );
};

/**
 * Input Field Component
 */
const InputField = ({ label, type, name, value, placeholder, onChange }) => (
  <div style={styles.inputGroup}>
    <label style={styles.label}>{label}</label>
    <input type={type} name={name} value={value} onChange={onChange} placeholder={placeholder} style={styles.input} required />
  </div>
);

/**
 * Text Area Field Component
 */
const TextAreaField = ({ label, name, value, placeholder, onChange }) => (
  <div style={styles.inputGroup}>
    <label style={styles.label}>{label}</label>
    <textarea name={name} value={value} onChange={onChange} placeholder={placeholder} style={styles.textarea} required />
  </div>
);

// Inline styles
const styles = {
  container: {
    maxWidth: "500px",
    margin: "50px auto",
    padding: "20px",
    borderRadius: "8px",
    backgroundColor: "#f9f9f9",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
    fontSize: "24px",
    color: "#333",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  inputGroup: {
    display: "flex",
    flexDirection: "column",
    gap: "5px",
  },
  label: {
    fontSize: "14px",
    color: "#555",
  },
  input: {
    padding: "10px",
    fontSize: "16px",
    borderRadius: "4px",
    border: "1px solid #ddd",
  },
  textarea: {
    padding: "10px",
    fontSize: "16px",
    borderRadius: "4px",
    border: "1px solid #ddd",
    resize: "vertical",
  },
  button: {
    padding: "10px",
    fontSize: "16px",
    borderRadius: "4px",
    backgroundColor: "#4caf50",
    color: "#fff",
    border: "none",
    cursor: "pointer",
  },
};

export default AddRecipe;
