import { useState, useEffect } from "react";
import axios from "axios";
import "./Home.css";
//import MyIngredients from "./myingredients"; 

const HomePage = () => {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [showIngredientsModal, setShowIngredientsModal] = useState(false);

  // Fetch Recipes on Mount
  useEffect(() => {
    axios
      .get("http://localhost:6409/api/recipestores")
      .then((response) => {
        console.log("Fetched Data:", response.data);
        setRecipes(response.data);
      })
      .catch((error) => console.error("Error fetching recipes:", error));
  }, []);

  // Function to Add a New Recipe
  const addRecipe = (newRecipe) => {
    axios
      .post("http://localhost:6409/api/recipestores", newRecipe)
      .then((response) => {
        setRecipes((prevRecipes) => [...prevRecipes, response.data]); // âœ… Append new recipe
      })
      .catch((error) => console.error("Error adding recipe:", error));
  };

  const openModal = (recipe) => {
    setSelectedRecipe(recipe);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedRecipe(null);
    setShowIngredientsModal(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Recipe Gallery</h1>

      {/* Button to Add a Recipe (Example) */}
      <button
        className="add-recipe-button"
        onClick={() =>
          addRecipe({
            name: "New Dish",
            image: "https://via.placeholder.com/150",
            description: "A delicious new recipe!",
            price: 15.99,
          })
        }
      >
        âž• Add Recipe
      </button>

      <div className="recipe-grid-container">
        {recipes.length > 0 ? (
          recipes.map((recipe) => (
            <div
              key={recipe._id}
              className="recipe-card"
              onClick={() => openModal(recipe)}
            >
              <img
                src={recipe.image}
                alt={recipe.name}
                className="recipe-image"
              />
              <div className="recipe-details">
                <h2 className="recipe-name">{recipe.name}</h2>
                <p className="recipe-description">{recipe.description}</p>
                <p className="recipe-price">${recipe.price}</p>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">Loading recipes...</p>
        )}
      </div>

      {/* Main Modal for Recipe Details */}
      {isModalOpen && selectedRecipe && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-button" onClick={closeModal}>âœ–</button>
            <h2 className="modal-title">{selectedRecipe.name}</h2>
            <img
              src={selectedRecipe.image}
              alt={selectedRecipe.name}
              className="modal-image"
            />
            <p className="modal-description">{selectedRecipe.description}</p>
            <p className="modal-price">Price: ${selectedRecipe.price}</p>

            {/* Watch Video Button */}
            {selectedRecipe.videoUrl && (
              <a
                href={selectedRecipe.videoUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="video-button"
              >
                ðŸŽ¥ Watch Video
              </a>
            )}

            {/* View Ingredients Button */}
            <button
              className="ingredients-button"
              onClick={() => setShowIngredientsModal(true)}
            >
              View Ingredients
            </button>
          </div>
        </div>
      )}

      {/* Ingredients Modal for Each Recipe */}
      {showIngredientsModal && selectedRecipe && (
        <MyIngredients
          ingredients={selectedRecipe.ingredients} 
          onClose={() => setShowIngredientsModal(false)}
        />
      )}
    </div>
  );
};

export default HomePage;
