import React from "react";
//import RecipeCard from "./RecipeCard";
import "./App.css";

const recipes = [
  {
    id: 1,
    title: "Spaghetti Carbonara",
    image: "https://source.unsplash.com/300x200/?spaghetti",
    description: "A classic Italian pasta dish with eggs, cheese, pancetta, and pepper.",
  },
  {
    id: 2,
    title: "Chicken Biryani",
    image: "https://source.unsplash.com/300x200/?biryani",
    description: "A flavorful and aromatic rice dish cooked with spices and chicken.",
  },
];

const App = () => {
  return (
    <div className="app">
      <h1>Food Recipe Collection</h1>
      <div className="recipe-container">
        {recipes.map((recipe) => (
          <RecipeCard key={recipe.id} recipe={recipe} />
        ))}
      </div>
    </div>
  );
};

export default App;
