import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './homepage.css';

const Homepage = () => {
  const [recipeStores, setRecipeStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [expandedCard, setExpandedCard] = useState(null); // store clicked card

  useEffect(() => {
    const fetchRecipeStores = async () => {
      try {
        const response = await axios.get('http://localhost:6409/api/recipestores');
        setRecipeStores(response.data);
      } catch (error) {
        console.error('Error fetching recipe stores:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipeStores();
  }, []);

  // Open expanded view
  const openCard = (store) => {
    setExpandedCard(store);
    document.body.style.overflow = 'hidden'; // prevent background scroll
  };

  // Close expanded view
  const closeCard = () => {
    setExpandedCard(null);
    document.body.style.overflow = 'auto';
  };

  return (
    <div className="homepage-container">
      <h1>Recipe Stores</h1>

      {loading ? (
        <p>Loading...</p>
      ) : recipeStores.length === 0 ? (
        <p>No recipe stores found.</p>
      ) : (
        <>
          <div className="recipe-grid">
            {recipeStores.map((store) => (
              <div 
                className="recipe-card" 
                key={store._id} 
                onClick={() => openCard(store)}
                tabIndex={0} // keyboard focus
                onKeyDown={(e) => { if (e.key === 'Enter') openCard(store); }}
                role="button"
                aria-label={`Open details for ${store.name}`}
              >
                <img src={store.image} alt={store.name} />
                <h3>{store.name}</h3>
                <p>{store.description}</p>
                <p><strong>Price:</strong> ₹{store.price}</p>
              </div>
            ))}
          </div>

          {/* Expanded Fullscreen Overlay */}
          {expandedCard && (
            <div className="overlay" onClick={closeCard} role="dialog" aria-modal="true" aria-labelledby="expanded-title">
              <div className="expanded-card" onClick={e => e.stopPropagation()}>
                <button className="close-btn" onClick={closeCard} aria-label="Close expanded view">&times;</button>
                <img src={expandedCard.image} alt={expandedCard.name} />
                <h2 id="expanded-title">{expandedCard.name}</h2>
                <p>{expandedCard.description}</p>
                <p><strong>Price:</strong> ₹{expandedCard.price}</p>
                {/* Add more details here if available */}
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Homepage;
